import React, { Component } from 'react';
import AppointmentItem from './AppointmentItem';
import propTypes from 'prop-types';
import '../css/Appointments.css';

class Boilers extends Component {
  render() {
    return (
      <div>
        <table>
          <caption>Appointments Records</caption>
          <tbody>
            <tr>
              <th>Id</th>
              <th>Building ID</th>
              <th>Boiler ID</th>
              <th>Starting Date</th>
              <th>Ending Date</th>
              <th>Maintenance Type</th>
              <th>Update</th>
              <th>Delete</th>
            </tr>
            {this.props.appointments.map(appointment => <AppointmentItem key={appointment.id} appointment={appointment} deleteAppointment={this.props.deleteAppointment} updateButton={this.props.updateButton} />)}
          </tbody>
        </table>
      </div>

    );
  }
}

Appointments.propTypes = {
  appointments: propTypes.array.isRequired
}

export default Appointments;
